      {/* Hero Image */}
      {project.image && (
        <div className="mb-12 rounded-2xl overflow-hidden border border-black/10">
          {project.url ? (
            <a href={project.url} target="_blank" rel="noopener noreferrer" className="block hover:opacity-90 transition">
